ContactSharedInfo::
ContactSharedInfo(Arcane::IItemFamily * family1, Arcane::IItemFamily * family2) 
{
  m_family1 = family1;
  m_family1_internals = m_family1->itemsInternal();
  m_family2 = family2;
  m_family2_internals = m_family2->itemsInternal();

  if (m_family1->itemKind() == Arcane::IK_Face and m_family2->itemKind() == Arcane::IK_Face)
    m_kind = IContactFamily::IK_FaceFace;
  else if (m_family1->itemKind() == Arcane::IK_Node and m_family2->itemKind() == Arcane::IK_Face)
    m_kind = IContactFamily::IK_NodeFace;
  else
    throw Arcane::NotImplementedException(A_FUNCINFO,"Not implemented contact type");
}

void ContactSharedInfo::
update()
{
    m_family1_internals = m_family1->itemsInternal();
    m_family2_internals = m_family2->itemsInternal();
}

