/*---------------------------------------------------------------------------*/

ContactVariable::
ContactVariable(IContactFamily * family)
  : m_family(family)
{
  m_family->registerVariable(this);
}

/*---------------------------------------------------------------------------*/
  
ContactVariable::
~ContactVariable()
{
  detachFromFamily();
}

/*---------------------------------------------------------------------------*/

void
ContactVariable::
detachFromFamily()
{
  if (m_family) {
    m_family->unregisterVariable(this);
    m_family = NULL;
  }
}

