void 
KozenyCarmannFunctionService::
init()
{
  if(m_initialized) return;

  info() << A_FUNCINFO;
  
  setVariableName(options()->variable());
  
  const String parameter_name = options()->parameter.name;

  setParameterName(parameter_name);

  setParameter(parameter_name,options()->parameter.value);

  m_initialized = true; ;
}

