#ifndef ARCGEOSIM_NUMERICS_EXPRESSIONS_UNSTEADYREGULARGRIDDATA_IUNSTEADYREGULARGRIDDATA_H
#define ARCGEOSIM_NUMERICS_EXPRESSIONS_UNSTEADYREGULARGRIDDATA_IUNSTEADYREGULARGRIDDATA_H
/* Author : dechaiss at Tue Apr 13 13:26:02 2010
 * Generated by createNew
 * IFunctionR4vR1 implementation dealing with values given on a regular Cartesian grid
 * for different times t.
 * Data are read in a file by method setPoints(file_name).
 * IUnsteadyRegularGridData f(x,y,z,t) = interpolates in space and time the given data.
 * The implementation is done in a template class UnsteadyRegularGridDataT<Interpolator>
 * Interface is created to avoid template manipulation to make it easier.
 */

#include <arcane/ArcaneTypes.h>
#include "ArcGeoSim/Numerics/Expressions/IFunctionR4vR1.h"

// Structure used to store grid type enumeration
struct RegularGridType
{
  enum eRegularGridType
  {
    UniformGrid, NonUniformGrid
  };

  enum class GridDimension {
	  eUndefined,
	  e2D,
	  e2DT,
	  e3D,
	  e3DT
  };
};

// Interface used to handle UnsteadyGridDataT<T>
class IUnsteadyRegularGridData : public IFunctionR4vR1
{
public:
  /** Constructeur de la classe */
  IUnsteadyRegularGridData()
    {
      ;
    }

  /** Destructeur de la classe */
  virtual ~IUnsteadyRegularGridData() { }

public:

  //! Initialisation
  virtual void init() = 0;

  //! Choix du type de grille
  virtual void setRegularGridType(const RegularGridType::eRegularGridType& grid_type) = 0;

  //! Enregistrement des points de la grille
  virtual RegularGridType::GridDimension setPoints(const String& file_name) = 0;

  //! Affichage de la grille pour le debug
  virtual void printGrid() const = 0;

  //! Getting file name
  virtual  String getFileName() const = 0;

  //! Getting grid type
  virtual  RegularGridType::eRegularGridType getGridType() const = 0;
};

#endif /* ARCGEOSIM_NUMERICS_EXPRESSIONS_UNSTEADYREGULARGRIDDATA_IUNSTEADYREGULARGRIDDATA_H */
