Arcane::Real
ArcNum::NewtonSolver::
newtonError()
{
  ARCANE_ASSERT((m_initialized),("Newton solver not initialized"));
  Alien::SimpleCSRLinearAlgebra alg;
  return alg.norm2(m_linear_system.rhs());
}

