Arcane::Real 
TimeStep::Setter::
nextTimeStep() const
{
  if(not m_dt_is_set)
    throw Arcane::FatalErrorException("Next time step has not been set");
  
  m_dt_is_set = false;

  return m_next_dt;
}
    
