#include <arcane/MathUtils.h>

/*---------------------------------------------------------------------------*/

/*---------------------------------------------------------------------------*/

bool
ArcGeoSim::ParallelUtils::
collectiveOr(const bool value)
{
  Integer v = (value)?1:0;
  v = m_parallel_mng->reduce(Arcane::Parallel::ReduceMax,v);
  return (v==1);
}

/*---------------------------------------------------------------------------*/

bool
ArcGeoSim::ParallelUtils::
collectiveOrder(const bool value, const Arcane::Integer master)
{
  Arcane::IntegerSharedArray v(1);
  v[0] = (value)?1:0;
  m_parallel_mng->broadcast(v,master);
  return (v[0]==1);
}

